import React, { useState } from 'react'


export default function TextForm(props) {

    let handleOnClick = () => {
        setText("")
    }

    let handleOnChange = (event) => {
        setText(event.target.value)
        console.log("On Change")
    }

    let handleUpClick = () => {
        console.log("Convert to Uppercase " + text)
        let newText = text.toUpperCase()
        setText(newText)
        // setText("You clicked Convert to Uppercase ")
    }
    const [text, setText] = useState('Enter text here ');
    return (
        <>
            <div>
                <h1>{props.heading}</h1>
                <div className="mb-3">
                    <textarea className="form-control" onClick={handleOnClick} onChange={handleOnChange} value={text} id="myBox" rows="8"></textarea>
                </div>
                <button className="btn btn-primary" onClick={handleUpClick}>Convert to Uppercase</button>
            </div>
            <div className="container my-5">
                <h1>Your Text Summary</h1>
                <p className='mt-4'>{text.split(" ").length} words and {text.length} characters</p>
                <p>Take {0.008 * text.split(" ").length} minutes to read</p>
            </div>
            <div>
                <h1>Preview</h1>
                
            </div>
        </>
    )
}
