import { useState } from 'react';
import './App.css';
import AboutSection from './components/about';
import Navbar from './components/navbar_props';
import TextForm from './components/textForm_state';
import Alert from './components/alert'
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';


function App() {

  const [mode, setMode] = useState('light')

  const [alert, setAlert] = useState(null)

  let showAlert = (type, message) => {
    setAlert({
      msg: message,
      typ: type
    })

    setTimeout(() => {
      setAlert(null)
    }, 2000)
  }

  let toggleMode = () => {
    if (mode === 'light') {
      setMode('dark')
      document.body.style.backgroundColor = 'black'
      document.body.style.color = 'white'
      showAlert("success", "The Dark Mode has been enabled.")
      document.title = "TextUtils - Dark Mode"
    }
    else {
      setMode('light')
      document.body.style.backgroundColor = 'white'
      document.body.style.color = 'black'
      showAlert("success", "The Light Mode has been enabled.")
      document.title = "TextUtils - Light Mode"
    }
  }

  return (
    <>
      <BrowserRouter>
        <Navbar title="TextUtils" about='About' mode={mode} toggleMode={toggleMode} />
        <Navbar mode={mode} toggleMode={toggleMode} />
        <Alert alert={alert} />
        <div className="container my-3">
          <Routes>
            <Route exact path='/about' element={<AboutSection />} />
            <Route path='/' element={<TextForm heading='Enter your text to analyze' showAlert={showAlert} mode={mode} />} />
          </Routes>
        </div>
      </BrowserRouter >
    </>
  );
}

export default App;
